// Custom SVG chart ‚Äî drawn manually for full control
export default function EuropeSeedRecords() {
  const [mode, setMode] = useState('dark');
  const [hoveredIdx, setHoveredIdx] = useState(null);
  const t = themes[mode];
  const font = "'Source Sans 3', 'Source Sans Pro', system-ui, -apple-system, sans-serif";

  // Chart dimensions
  const W = 820, H = 360;
  const padL = 70, padR = 160, padT = 20, padB = 40;
  const chartW = W - padL - padR;
  const chartH = H - padT - padB;

  // Map year ‚Üí x (linear from 2000 to 2026)
  const minYear = 2000, maxYear = 2026;
  const xScale = yr => padL + ((yr - minYear) / (maxYear - minYear)) * chartW;

  // Linear scale for Y (0 to 1000)
  const yMax = 1100;
  const yScale = v => padT + chartH - (v / yMax) * chartH;

  // Y axis ticks
  const yTicks = [0, 200, 400, 600, 800, 1000];

  // Step line path (horizontal then vertical ‚Äî staircase connecting records)
  let stepPath = '';
  data.forEach((d, i) => {
    const x = xScale(d.year);
    const y = yScale(d.value);
    if (i === 0) {
      stepPath += `M ${x} ${y}`;
    } else {
      const prevX = xScale(data[i - 1].year);
      stepPath += ` L ${x} ${yScale(data[i - 1].value)} L ${x} ${y}`;
    }
  });

  // Bubble radius scaled by value (linear)
  const bubbleR = v => {
    const minR = 6, maxR = 32;
    return minR + (v / 1000) * (maxR - minR);
  };

  const isRecord = (d) => d.value === 1000 || d.value === 150;

  return (
    <div style={{
      background: t.bg, minHeight: '100vh', display: 'flex',
      alignItems: 'center', justifyContent: 'center', padding: 24,
      fontFamily: font, transition: 'all 0.3s ease',
    }}>
      <style>{`@import url('https://fonts.googleapis.com/css2?family=Source+Sans+3:wght@400;500;600;700;900&display=swap');`}</style>
      <div style={{
        background: t.card, borderRadius: 20, border: `1px solid ${t.border}`,
        padding: '48px 52px 40px', maxWidth: 980, width: '100%',
        transition: 'all 0.3s ease',
      }}>
        {/* Header */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: 12 }}>
          <span style={{
            background: 'rgba(62,236,210,0.08)', color: t.accent, padding: '4px 12px',
            borderRadius: 20, fontSize: 12, fontWeight: 600,
            textTransform: 'uppercase', letterSpacing: '0.05em',
            border: `1px solid rgba(62,236,210,0.2)`,
          }}>
            üåç European Ecosystem
          </span>
          <div style={{ display: 'flex', background: t.toggleBg, borderRadius: 8, padding: 3 }}>
            {['dark', 'light'].map(m => (
              <button key={m} onClick={() => setMode(m)} style={{
                background: mode === m ? t.toggleActive : 'transparent',
                color: mode === m ? t.toggleActiveText : t.toggleOffText,
                border: 'none', borderRadius: 6, padding: '5px 14px',
                fontSize: 12, fontWeight: 600, cursor: 'pointer',
                textTransform: 'uppercase', letterSpacing: '0.04em', fontFamily: font,
              }}>
                {m === 'dark' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
              </button>
            ))}
          </div>
        </div>

        <h1 style={{
          color: t.text, fontSize: 36, fontWeight: 900, letterSpacing: '-0.03em',
          lineHeight: 1.1, margin: 0, fontFamily: font,
        }}>
          Europe's Waited 18 Years for a New Record Seed Round
        </h1>
        <p style={{
          color: t.textSec, fontSize: 15, fontWeight: 400, margin: '12px 0 0',
          lineHeight: 1.5, fontFamily: font,
        }}>
          Record-setting European seed rounds since 2000
        </p>

        {/* SVG Chart */}
        <div style={{ marginTop: 32, overflowX: 'auto' }}>
          <svg width="100%" viewBox={`0 0 ${W} ${H}`} style={{ display: 'block', fontFamily: font }}>

            {/* Y grid lines + labels */}
            {yTicks.map(v => {
              const y = yScale(v);
              return (
                <g key={v}>
                  <line x1={padL} x2={W - padR} y1={y} y2={y}
                    stroke={t.grid} strokeWidth={1} />
                  <text x={padL - 8} y={y + 4} textAnchor="end"
                    fill={t.textMuted} fontSize={11} fontFamily={font}>
                    {v >= 1000 ? '$1B' : `${v}M`}
                  </text>
                </g>
              );
            })}

            {/* X axis tick labels ‚Äî only show years that have data points */}
            {data.map(d => (
              <text key={d.year} x={xScale(d.year)} y={H - padB + 18}
                textAnchor="middle" fill={t.textSec} fontSize={11} fontFamily={font} fontWeight={500}>
                {d.year}
              </text>
            ))}

            {/* Step line */}
            <path d={stepPath} fill="none" stroke={t.lineDash} strokeWidth={1.5} strokeDasharray="4 3" />

            {/* Bubbles + labels */}
            {data.map((d, i) => {
              const x = xScale(d.year);
              const y = yScale(d.value);
              const r = bubbleR(d.value);
              const isHov = hoveredIdx === i;
              const dimmed = hoveredIdx !== null && !isHov;
              const accent = d.value === 1000 ? '#EF4444' : d.value === 150 ? '#8B5CF6' : t.accent;

              // Label positioning ‚Äî nudge to avoid overlap
              const isIneffable = d.value === 1000;
              const labelRight = i === 3;
              const labelBelow = [0, 1, 2].includes(i) || isIneffable;
              const lx = labelRight ? x + r + 6 : x;
              const ly = labelBelow ? y + r + 16 : y - r - 14;
              const anchor = labelRight ? 'start' : 'middle';

              return (
                <g key={i}
                  onMouseEnter={() => setHoveredIdx(i)}
                  onMouseLeave={() => setHoveredIdx(null)}
                  style={{ cursor: 'pointer' }}
                  opacity={dimmed ? 0.2 : 1}>
                  {/* Glow ring */}
                  <circle cx={x} cy={y} r={r + 4}
                    fill={accent} fillOpacity={isHov ? 0.15 : 0.07} />
                  {/* Main bubble */}
                  <circle cx={x} cy={y} r={r}
                    fill={accent} fillOpacity={0.18}
                    stroke={accent} strokeWidth={isHov ? 2.5 : 1.8} />
                  {/* Centre dot */}
                  <circle cx={x} cy={y} r={4} fill={accent} />

                  {/* Company label */}
                  <text x={lx} y={ly}
                    textAnchor={anchor} fill={accent}
                    fontSize={isHov || d.value === 1000 ? 15 : d.value === 150 ? 14 : 11}
                    fontWeight={isHov || d.value >= 150 ? 700 : 600}
                    fontFamily={font}>
                    {d.company}
                  </text>
                  {/* Value label */}
                  <text x={lx} y={ly + 14}
                    textAnchor={anchor} fill={t.textMuted}
                    fontSize={10} fontWeight={500} fontFamily={font}>
                    {d.value >= 1000 ? '$1B' : `$${d.value}M`} ¬∑ {d.year}
                  </text>
                </g>
              );
            })}
          </svg>
        </div>

        {/* Footer */}
        <div style={{
          display: 'flex', justifyContent: 'space-between', alignItems: 'center',
          marginTop: 24, paddingTop: 16, borderTop: `1px solid ${t.footerBorder}`,
        }}>
          <span style={{ color: t.textMuted, fontSize: 12, fontFamily: font }}>Source: Dealroom.co</span>
          <DealroomLogo variant={t.logoVariant} />
        </div>
      </div>
    </div>
  );
}
